
public class NumberSystem {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int num= 30;
    if(num>0) {
    	System.out.println("Positive");}
    else if(num==0) {
    	System.out.println("Zero");}
    else if(num<0) {
    	System.out.println("Negative");}
    
	}

}
